export class Reservation {
  constructor(userId, reservationId, roomId, checkin, checkout, roomNumber, roomPrice, additions, breakSwitch, towelSwitch, poolSwitch) {
    this.additions = [];
    this.cashOut = 0;
    this.userId = userId;
    this.reservationId = reservationId;
    this.roomId = roomId;
    this.checkin = checkin;
    this.checkout = checkout;
    this.roomNumber = roomNumber;
    this.roomPrice = roomPrice;
    this.additions = additions;
    this.breakSwitch = breakSwitch;
    this.towelSwitch = towelSwitch;
    this.poolSwitch = poolSwitch;
  }

