export class User {
  constructor(id, userName, rewards) {
    this.id = id;
    this.userName = userName;
    this.rewards = rewards;
  }

  addPoints(points) {
    this.rewards += points;
  }

  subtractPoints(points) {
    this.rewards -= points;
  }

}
